SQLStoredProcedureSpec.CreateProcedureSQL:
create procedure dbo.USR_USP_RECORD_ADDRESS_CHANGE ( @ID uniqueidentifier output ,@ADDRESSID uniqueidentifier ,@CHANGETYPE nvarchar(100) ,@CHANGEAGENTID uniqueidentifier ) as begin --accept the ID of a constituent who has changed. --do something to record the change.
set @id = isnull(@id,newid()); declare @now datetime = getdate();
INSERT INTO dbo.USR_CHANGELOG_ADDRESS (ID,ADDRESSID,MESSAGE,CHANGETYPE,ADDEDBYID,CHANGEDBYID,DATEADDED,DATECHANGED) VALUES (@ID,@ADDRESSID,'Change recorded',@CHANGETYPE,@CHANGEAGENTID,@CHANGEAGENTID,@NOW,@NOW); end
